# Standard Librariesimport datetime# Third Party Librariesimport factoryfrom django.utils import timezonefk = factory.Fakertz = timezone.tzinfofrom mapistar.pony_backend import dbdef import_pony_models(pony_settings):    from importlib import import_module    entities_filename = pony_settings.get('entities_filename', "models")    for app in pony_settings['INSTALLED_APPS']:        import_module('.'.join((pony_settings['PROJECT_NAME'], app,                                entities_filename)))def pony_init():    from config import settings    if not db.entities:        import_pony_models(settings.PONY)        db.connect(**settings.PONY['DATABASE'])pony_init()class FacPatient(factory.PonyFactory):    class Meta:        model = db.Patient    nom = fk('last_name', locale="fr_FR")    prenom = fk('first_name', locale="fr_FR")    ddn = fk('past_date', start_date="-30y", tzinfo=datetime.tzinfo())    street = fk('street_address', locale="fr_FR")# class FacPatient(factory.django.DjangoModelFactory):#     class Meta:#         model = Patient#     name = fk('last_name', locale="fr_FR")#     firstname = fk('first_name', locale="fr_FR")#     birthdate = fk('past_date', start_date="-30y", tzinfo=datetime.tzinfo())#     street = fk('street_address', locale="fr_FR")#     city = fk('city', locale="fr_FR")#     postalcode = fk('zipcode')#     phonenumber = "0381395685"#     email = fk('email', locale="fr_FR")#     alive = True#     sexe = fk('pybool')